    public static void main(String[] args) {
        partOne();
        partTwo();
    }

    private static void partOne(){
        System.out.println("Santa ended up on floor " + (getCount("(", input) - getCount(")", input)));
    }

    private static void partTwo(){
        int floor = 0;
        for (int i = 0; i < input.length(); i++) {
            String sub = input.substring(i, i + 1);
            if(sub.equals("(")){
                floor++;
            }else{
                floor--;
            }

            if(floor <= -1){
                System.out.println("Santa entered basement at index " + (i + 1));
                break;
            }
        }
    }

    private static int getCount(String match, String input){
        if(match.length() > input.length()){
            return 0;
        }

        int count = 0;
        for (int i = 0; i < input.length() - match.length() + 1; i++) {
            if(input.substring(i, i + match.length()).equals(match)){
                count++;
            }
        }
        return count;
    }

